sys_script_client.script:
//displays a confirmation message when the user changes State value to Closed Complete
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
var val = g_form.getValue('state');
	if (val == 3){
	var answer = confirm("Please confirm you entered the YouTube video link to this assignment in the Work Notes");
		if (answer == false){
			g_form.setValue('state',oldValue);		
	}
	}
}
